import React from "react";
import emailjs from "@emailjs/browser";
import { Form, Input, TextArea, Button } from "semantic-ui-react";
import Swal from "sweetalert2";


import "./Contact.css";

//const SERVICE_ID = "service_9tx1fam";
//const TEMPLATE_ID = "template_9m0ahfq";
//const USER_ID = "NNsXG2hJujR8UrYuP";

const App = () => {
  const handleOnSubmit = (e) => {
    e.preventDefault();
    emailjs.sendForm("service_9tx1fam", "template_9m0ahfq", e.target, "NNsXG2hJujR8UrYuP")
      .then((result) => {
        console.log(result.text);
        Swal.fire({
          icon: "success",
          title: "Message Sent Successfully"
        })
      }, (error) => {
        console.log(error.text);
        Swal.fire({
          icon: "error",
          title: "Ooops, something went wrong",
          text: error.text,
        })
      });
    e.target.reset()
  };

return (
  <div style={{"background-color":"black"}}>
    <Row className="mb-5 mt-3">
          <Col lg="8">
            <h1 className="display-4 mb-4">Contact Me</h1>
            <hr className="t_border my-4 ml-0 text-left" />
          </Col>
        </Row>
    <div className="App" style={{"background-color":"aliceblue"}}>
      <Form onSubmit={handleOnSubmit}>
        <Form.Field
          id="form-input-control-email"
          control={Input}
          label="Email"
          name="user_email"
          placeholder="Email…"
          required
          style = {{
          icon:"mail",
          iconPosition:"left",
          }}
        />
        <Form.Field
          id="form-input-control-last-name"
          control={Input}
          label="Name"
          name="user_name"
          placeholder="Name…"
          required
          style={{
          icon:"user circle",
          iconPosition:"left",
          padding: "7px"
        }}
        />
        
        <Form.Field
          id="form-textarea-control-opinion"
          control={TextArea}
          label="Message"
          name="user_message"
          placeholder="Message…"
          required
        />
        <Button type='submit' style = {{color:"black",cursor: "pointer",width: "30%","margin-top": "1rem"}}><strong>Submit</strong></Button>
      </Form>
    </div>
    </div>
  );
}

export default App;
